const pantoneColors = {
    '2017': '#88B04B',
    '2018': '#604c8d',
    '2019': '#FA7268',
    '2020': '#34558b',
    '2021': {bg: '#939597', text: '#F5DF4D'},
    '2022': '#6667AB',
    '2023': '#BB2649',
    '2024': '#FFBE98',
    '2025': '#A47864'
};

$(document).ready(function() {
    const elements = 'h1,h2,h3,h4,h5,h6,p,a,.text-large';
    Object.keys(pantoneColors).forEach(year => {
        $(`#${year}`).on('click', function() {
            const color = pantoneColors[year];
            const bgColor = typeof color === 'object' ? color.bg : color;
            const textColor = typeof color === 'object' ? color.text : '#f4f4f9';
            
            $('html, body').css('background', bgColor);
            $(elements).css('color', textColor);
            $('.svg-mark').css('fill', textColor);
        });
    });
});