const pantoneColors={'2025':{background:'#a47764',foreground:'#f4f4f9'},'2024':{background:'#ffbe98',foreground:'#f4f4f9'},'2023':{background:'#bb2649',foreground:'#f4f4f9'},'2022':{background:'#6667ab',foreground:'#f4f4f9'},'2021':{background:'#f5df4d',foreground:'#939597'},'2020':{background:'#0f4c81',foreground:'#f4f4f9'},'2019':{background:'#ff6f61',foreground:'#f4f4f9'},'2018':{background:'#5f4b8b',foreground:'#f4f4f9'},'2017':{background:'#88b04b',foreground:'#f4f4f9'}};const pantoneDarkColors={'2025':{background:'#0d0d0e',foreground:'#a47764'},'2024':{background:'#0d0d0e',foreground:'#ffbe98'},'2023':{background:'#0d0d0e',foreground:'#bb2649'},'2022':{background:'#0d0d0e',foreground:'#6667ab'},'2021':{background:'#939597',foreground:'#f5df4d'},'2020':{background:'#0d0d0e',foreground:'#0f4c81'},'2019':{background:'#0d0d0e',foreground:'#ff6f61'},'2018':{background:'#0d0d0e',foreground:'#5f4b8b'},'2017':{background:'#0d0d0e',foreground:'#88b04b'}};function applyColorScheme(year){const isDarkMode=document.body.classList.contains('dark-mode');const colors=isDarkMode&&pantoneDarkColors[year]?pantoneDarkColors[year]:pantoneColors[year];if(!colors)return;const root=document.documentElement;document.body.style.backgroundColor=colors.background;root.style.setProperty('--text-color',colors.foreground);root.style.setProperty('--border-color',colors.foreground);const textElements=document.querySelectorAll('.name, .intro-text, .intro-text p, .section h2, .section p, .links, .links a, .footer, .footer p, .pantone-box h2, .pantone-years, .pantone-years p');textElements.forEach(el=>{el.style.color=colors.foreground});const logoPath=document.querySelectorAll('.logo path');logoPath.forEach(path=>{path.style.fill=colors.foreground});const pantoneBox=document.querySelector('.pantone-box');if(pantoneBox){pantoneBox.style.borderColor=colors.foreground}localStorage.setItem('selectedPantoneYear',year)}function resetColors(){const root=document.documentElement;document.body.style.backgroundColor='';const textElements=document.querySelectorAll('.name, .intro-text, .intro-text p, .section h2, .section p, .links, .links a, .footer, .footer p, .pantone-box h2, .pantone-years, .pantone-years p');textElements.forEach(el=>{el.style.color=''});const logoPath=document.querySelectorAll('.logo path');logoPath.forEach(path=>{path.style.fill=''});const pantoneBox=document.querySelector('.pantone-box');if(pantoneBox){pantoneBox.style.borderColor=''}localStorage.removeItem('selectedPantoneYear');const savedDarkMode=localStorage.getItem('darkMode');if(savedDarkMode==='true'){document.body.classList.add('dark-mode')}}function applyDarkMode(){document.body.classList.add('dark-mode');localStorage.setItem('darkMode','true');const savedYear=localStorage.getItem('selectedPantoneYear');if(savedYear){applyColorScheme(savedYear)}}function removeDarkMode(){document.body.classList.remove('dark-mode');localStorage.setItem('darkMode','false');const savedYear=localStorage.getItem('selectedPantoneYear');if(savedYear){applyColorScheme(savedYear)}}function toggleDarkMode(){if(document.body.classList.contains('dark-mode')){removeDarkMode()}else{applyDarkMode()}}document.addEventListener('DOMContentLoaded',function(){const pantoneYears=document.querySelector('.pantone-years');const logo=document.querySelector('.logo');const darkModeToggle=document.getElementById('darkModeToggle');const savedDarkMode=localStorage.getItem('darkMode');if(savedDarkMode==='true'){document.body.classList.add('dark-mode')}if(darkModeToggle){darkModeToggle.addEventListener('click',toggleDarkMode)}if(pantoneYears){const yearElements=pantoneYears.querySelectorAll('p');yearElements.forEach(yearEl=>{const year=yearEl.textContent.trim();yearEl.style.cursor='pointer';yearEl.addEventListener('click',function(){yearElements.forEach(el=>el.classList.remove('active-year'));this.classList.add('active-year');applyColorScheme(year)});yearEl.addEventListener('mouseenter',function(){if(!this.classList.contains('active-year')){this.style.opacity='0.6'}});yearEl.addEventListener('mouseleave',function(){this.style.opacity='1'})});const savedYear=localStorage.getItem('selectedPantoneYear');if(savedYear){const yearEl=Array.from(yearElements).find(el=>el.textContent.trim()===savedYear);if(yearEl){yearEl.classList.add('active-year');applyColorScheme(savedYear)}}}if(logo){logo.style.cursor='pointer';logo.addEventListener('click',function(){const yearElements=document.querySelectorAll('.pantone-years p');yearElements.forEach(el=>el.classList.remove('active-year'));resetColors()})}window.resetPantoneColors=resetColors});
